INSERT INTO screen_type (id, name, description) VALUES (1, 'CRISPR', 'made using CRISPR without added DNA or oligos');
INSERT INTO screen_type (id, name, description) VALUES (2, 'CRISPR (HDR)', 'made using homology-directed repair or homologous recombination');
INSERT INTO screen_type (id, name, description) VALUES (3, 'CRISPR (non-HR)', 'CRISPR-mediated insertion of DNA without homology to target sequence. E.g. Higashijima-style insertion.');
INSERT INTO screen_type (id, name, description) VALUES (4, 'ENU', 'identified in a forward genetic screen of chemically-mutagenized fish');
INSERT INTO screen_type (id, name, description) VALUES (5, 'Insertional Mutagenesis', 'made by the random insertion of a DNA element (e.g. retrovirus or transposon) that acts as a mutagen rather than a reporter.');
INSERT INTO screen_type (id, name, description) VALUES (6, 'TALEN', 'made using TALENs');
INSERT INTO screen_type (id, name, description) VALUES (7, 'TILLING', 'identified by TILLING of ENU-mutagenized fish');
INSERT INTO screen_type (id, name, description) VALUES (8, 'CRISPR Pair', 'TBD');
